// Student.js

import React from "react";

function Student({ stdName, setStdName, stdId, setStdId }) {
  const handleSearchStudent = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch("http://localhost:3001/search-student", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ stdId }),
      });

      const data = await response.json();

      if (data.success) {
        setStdName(data.student.stdName);
      } else {
        setStdName("");
        alert(data.message);
      }
    } catch (error) {
      console.error("Error searching for student:", error.message);
    }
  };

  return (
    <form onSubmit={handleSearchStudent}>
      <label htmlFor="stdID">
        <input
          type="number"
          name="stdID"
          placeholder="71772217101"
          onChange={(e) => setStdId(e.target.value)}
          required
        />
      </label>
      <p>{stdName}</p>
    </form>
  );
}

export default Student;
