import React from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import {Form,Card} from 'react-bootstrap/Form';

function Sem({ selectedSem, setSelectedSem }) {
  const handleChange = (e) => {
    setSelectedSem(e.target.value);
  }

  return (
    <Card className="m-4 p-4 shadow">
    <Form.Group controlId="semSelect">
      <Form.Label column sm="2" className="fw-bold">Select Semester:</Form.Label>
        <Form.Select
              className="form-select select-dropdown"
              value={selectedSem}
              onChange={handleChange}
            >
        <option value='III'>III</option>
        <option value='IV'>IV</option>
        </Form.Select>
    </Form.Group>
    </Card>
  );
}


export default Sem;
