import React, { useEffect, useState } from "react";
import './Main.css';

const Admin2 = () => {
    const [std, setStd] = useState([]);
    const [flag, setFlag] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const stdListResponse = await fetch('http://localhost:3001/studentList');
                const stdList = await stdListResponse.json();
                console.log(stdList);

                // Set flag state
                setFlag(stdList.map(student => ({ Name: student.StdName, RegNo: student.RegNo })));

                // Fetch and update the number of courses submitted for each student
                const coursesPromises = flag.map(async (std) => {
                    const res = await fetch(`http://localhost:3001/student/admin/${std.Name}`);
                    const fin = await res.json();
                    console.log(fin);

                    // Return an object with student info and number of courses submitted
                    return { ...std, CoursesSubmitted: fin.length };
                });

                // Wait for all promises to resolve
                const studentsWithCourses = await Promise.all(coursesPromises);

                // Set the state with the updated information
                setStd(studentsWithCourses);
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, [flag]); // Add flag to dependency array to ensure useEffect runs when flag changes

    return (
        <table className="table">
            <thead>
                <tr>
                    <th>Register No</th>
                    <th>Student Name</th>
                    <th>No of Courses Submitted</th>
                </tr>
            </thead>
            <tbody>
                {std.map((student) => (
                    <tr key={student.RegNo}>
                        <td>{student.RegNo}</td>
                        <td>{student.Name}</td>
                        <td>{student.CoursesSubmitted}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default Admin2;
