import React, { useState } from "react";

function Student({ setStdName, setStdId }) {
  const [stdIdInput, setStdIdInput] = useState("");

  const handleSearchStudent = async (e) => {
    e.preventDefault();
    
    // Assuming you have already retrieved the student name on form submission
    const stdName = prompt("Please enter your name:");
    
    if (stdName) {
      setStdName(stdName);
      setStdId(stdIdInput);
    } else {
      setStdName('');
      setStdId('');
    }
  };

  return (
    <form onSubmit={handleSearchStudent}>
      <label htmlFor='stdID'>
        <input
          type='text'
          name='stdID'
          placeholder='71772217101'
          value={stdIdInput}
          onChange={(e) => setStdIdInput(e.target.value)}
          required
        />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}

export default Student;
