import React, { useEffect, useState } from "react";
import './Main.css';

const Admin2 = () => {
    const [std, setStd] = useState([]);
    const [flag, setflag] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const stdListResponse = await fetch('http://localhost:3001/studentList');
                const stdList = await stdListResponse.json();
                console.log(stdList);
                setflag(stdList.map(student => ({ Name: student.StdName, RegNo: student.RegNo })));
                setStd(flag);
                console.log(Std);
                flag.forEach(async (std) => {
                    const res = await fetch(`http://localhost:3001/student/admin/${std}`)
                    console.log(res);
                })
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <table className="table">
            <thead>
                <tr>
                    <th>Register No</th>
                    <th>Student Name</th>
                    <th>No of Courses Submitted</th>
                </tr>
            </thead>
            <tbody>
               {std.map((student) => (
                    <tr key={student.RegNo}>
                        <td>{student.RegNo}</td>
                        <td>{student.Name}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default Admin2;
