import React from "react";

function Student({stdName,setStdName,stdId,setStdId}){
    const handleIDChange = (e) => {
        setStudent(e.target.value);
    }
    const handleSearchStudent = async () => {
        try {
          const response = await fetch('http://localhost:3001/search-student', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ stdId }),
          });
    
          const data = await response.json();
    
          if (data.success) {
            setStdName(data.student.stdName);
          } else {
            setStdName('');
            alert(data.message);
          }
        } catch (error) {
          console.error('Error searching for student:', error.message);
        }
      };
    return(
        <form onSubmit={handleSearchStudent}>
            <label htmlFor='stdID'>
                <input type='number' name='stdID' placeholder='71772217101' onChange={(e) => handleIDChange(e)} required />
            </label>
            <p>{stdName}</p>
        </form>
    );
}

export default Student;
