import React from "react";
import './Auth.css';

const Auth = ({regNo, setRegNo, dob, setDob, isAuth, setIsAuth, stdName, setStdName}) => { 
  const handleSubmit = (e) => {
    e.preventDefault();
    fetch('http://localhost:3001/api/studentID',
    {
      method: 'POST',
      headers: {
        'Content-Type' : 'application/json',
      },
      body: JSON.stringify({regNo,dob}),
    })
    .then((res) => res.json())
    .then((res) => {
      if(res!=="Wrong password"){
        setIsAuth(true);
        setStdName(res.StdName);
        localStorage.setItem('isAuth',true);
        localStorage.setItem('studentId', regNo);
      }
      else{
        setIsAuth(false);
      }
    })
    .catch((err) => console.log(err))
  } 
  return (
    <div className="bg-image" style={{backgroundImage: 'url(https://mdbootstrap.com/img/new/textures/full/171.jpg)', height: '300px'}}>
      <div className="card-container">
        <div className='card'>
          <div className='card-body'>
            <h2 className="card-title fw-bold mb-5">Sign up now</h2>
            
            <div className='input-group mb-4'>
              <label>Register Number : </label>
              <input
                value={regNo}
                onChange={(e) => setRegNo(e.target.value)}
                type="text"
                required
              />
            </div>

            <div className='input-group mb-4'>
              <label>Date Of Birth : </label>
              <input
                value={dob}
                onChange={(e) => setDob(e.target.value)}
                type="text"
                placeholder="DD-MM-YYYY"
                required
              />
            </div>

            <button className='btn-signup' type="submit">Login</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Auth;
