import React, { useState } from "react";

const Auth = () => {
    const [regNo,setRegNo] = useState("");
    const [dob,setDob] = useState("");
    const handleSubmit = (e) => {
        e.preventDefault();
        fetch('http://localhost:3001/auth-data',{
            method: 'POST',
            headers : {
                'Content-type' : 'application/json',
            },
            body: JSON.stringify({regNo,dob}),
        })
        .then((res) => res.json())
        .then((res) => {
            console.log(res);
        })
        .catch((err) => console.log(err))
    }
    return(
        <div className="auth">
            <form className="auth-form" onSubmit={handleSubmit}>
                <label htmlFor="authRegNo">Register Number : </label>
                <input 
                    id="authRegNo"
                    name="authRegNo"
                    value={regNo}
                    onChange={(e) => setRegNo(e.target.value)}
                    type="text"
                        />
                <label htmlFor="authDOB">Date Of Birth : </label>
                <input
                    id="authDOB"
                    name="authDOB"
                    value={dob}
                    onChange={(e) => setDob(e.target.value)}
                    type="text"
                        />
                <button type="submit">Verify</button>
            </form>
        </div>
    )
}

export default Auth;
