import React, { useEffect, useState } from "react";
import './App.css';

const Admin2 = () => {
    const [std, setStd] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const stdListResponse = await fetch('http://localhost:3001/studentList');
                const stdList = await stdListResponse.json();
                console.log(stdList);
                
                // Use setStd to update the state with the entire array
                setStd(stdList.map(student => ({ Name: student.StdName, RegNo: student.RegNo })));
            } catch (error) {
                console.error("Error fetching data:", error);
            }
        };

        fetchData();
    }, []);

    return (
        <table>
            <thead>
                <tr>
                    <th>Register No</th>
                    <th>Student Name</th>
                </tr>
            </thead>
            <tbody>
               {std.map((student) => (
                    <tr key={student.RegNo}>
                        <td>{student.RegNo}</td>
                        <td>{student.Name}</td>
                    </tr>
                ))}
            </tbody>
        </table>
    );
};

export default Admin2;
