// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Auth from './Auth';
import Main from './Main';

const App = () => {
  const [isAuth,setIsAuth] = useState(false);
  const [regNo,setRegNo] = useState("");
  const [dob,setDob] = useState("");
  const checkfunction = () => {
    const localAuth = localStorage.getItem('isAuth');
    if(localAuth)
      setIsAuth(true);
    else  
      setIsAuth(false);
  }

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={checkfunction() ? <Navigate to="/main" /> : <Auth regNo={regNo} setRegNo={setRegNo} dob={dob} setDob={setDob} isAuth={isAuth} setIsAuth={setIsAuth} />}
        />
        <Route
          path="/main"
          element={isAuth ? <Main regNo={regNo} setRegNo={setRegNo} dob={dob} setDob={setDob} isAuth={isAuth} setIsAuth={setIsAuth} /> : <Navigate to="/" />}
        />
      </Routes>
    </Router>
  );
};

export default App;
